def generateVaribleName():    
    leftWordsLength=len(leftWordList)
    leftAdjInd = random.randrange(leftWordsLength)
    leftWordList[leftAdjInd], leftWordList[leftWordsLength-1] = leftWordList[leftWordsLength-1],leftWordList[leftAdjInd]
    leftWord = leftWordList.pop()
    
    rightWordsLength=len(rightWordList)
    rightNounInd = random.randrange(rightWordsLength)
    rightWordList[rightNounInd], rightWordList[rightWordsLength-1] = rightWordList[rightWordsLength-1],rightWordList[rightNounInd]
    rightWord = rightWordList.pop()
    return f"{leftWord}_{rightWord}"

def changeVariables(fileData):
    allVariables = re.findall(r"(\w+)(?=( |)=( |))", fileData)
    for i in range(len(allVariables)):
        enrichedName = generateVaribleName()
        fileData = re.sub(r"(?:((?<=[^.)])|(?<=\A)))(\b{}\b)".format(allVariables[i][0]), enrichedName, fileData)
    return fileData

def runner(options):
    filePath = options.file or options.input
    with open(filePath, "r") as reader:
        readData = reader.read()
        changedData = changeVariables(readData)
    if options.replace:
        with open(filePath, "w+") as writer:
            writer.write(changedData)
    else:
        print(changedData)


parser = argparse.ArgumentParser(description="Python Variable Changer Script")
parser.add_argument("-r", "--replace", help="Replace file", required=False, default=False, type=bool)
requiredNamedGroup = parser.add_argument_group('required named arguments (mutually exlusive)')
requiredNamed = requiredNamedGroup.add_mutually_exclusive_group(required=True)
requiredNamed.add_argument("-i", "--input", help="Input file name", type=str)
requiredNamed.add_argument("-f", "--file", help="Alternative argument for input file name", type=str)

options = parser.parse_args()
runner(options)